/** Accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor utilities/Clock
 *  @parameter interval The interval between outputs in milliseconds.
 *  @output output Output for the counting sequence, of type number.
 *  @author Edward A. Lee
 *  @version $$Id$$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals clearInterval, exports, require, setInterval */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.parameter('interval', {
        'type': 'number',
        'value': 1000
    });
    this.output('output', {
        'type': 'number'
    });
};

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.initialize = function () {
    count = 0;
    // Need to record 'this' for use in the callback.
    var thiz = this;

    // Send an output and then call setInterval
    thiz.send('output', count);
    count += 1;

    handle = setInterval(function () {
        thiz.send('output', count);
        count += 1;
    }, this.getParameter('interval'));
};

exports.wrapup = function () {
    if (handle) {
        clearInterval(handle);
        handle = null;
    }
};

exports.setup = function() {
	this.extend('utilities/MutableBase');
	this.implement('ControllableSensor');
};

var httpClient = require('@accessors-modules/http-client');

var IdToResource = {
    '127': 'PressureSensor.js',
    '435': 'VibrationSensor.js',
    '105': 'TemperatureSensor.js'   
    //'435': 'VibrationSensor.js',

};

exports.setup = function() {
    // Inputs
    this.input('tags');
    this.input('id', {
        'type':'int', 
        'value':-1
    });

    // Outputs
    this.output('accessor', {
        'type': 'string',
        'value': ''
    });
    this.output('translation');
};

exports.initialize = function() {
    var thiz = this;
    var previousId = -1;

    this.addInputHandler('tags', function() {
        var tags = thiz.get('tags');
        var id = thiz.get('id');

        if (id != previousId) {
            // Index has changed. Look up an accessor and produce it.
            if (tags && tags.length > 0) {
                var file = IdToResource[id];
                if (file) {
                    var accessor = getResource(file, 2000);
                    thiz.send('accessor', accessor);
                } else {
                    thiz.send('accessor', null);
                }
            } else {
                thiz.send('accessor', null);
            }
            previousId = id;
        }
        if (tags && tags.length > 0) {
            for(var i = 0; i < tags.length; i++){
                if(tags[i].id == id){
                    thiz.send('translation', tags[i].center);
                    break;
                }
            }
        }
    });

    this.addInputHandler('id', function() {
        var tags = thiz.get('tags');
        var id = thiz.get('id');
		// If there are no tags and the id is negative,
		// then send null to unreify previous accessor.
		if (!tags && id < 0) {
            thiz.send('accessor', null);
            previousId = id;	
		}
    });
};

// The spec input is expected to be HTML containing
// a form to overlay on the image.
exports.setup = function() {
	this.input('reset', {'type':'string'});
	this.input('translate');
	this.input('data');
	this.input('spec', {'type':'JSON'});
	this.input('image');
	this.output('html', {'type':'string'});
	this.output('resources');
	this.output('update', {'type':'JSON'});
}

exports.initialize = function() {
	var self = this;
	var htmlSent = false;

	this.addInputHandler('reset', function() {
		var reset = this.get('reset');
		if (reset == 'new') {
			self.send('update', {
            	'id': 'output',
            	'property': 'html',
            	'content': '<b>Looking for device</b>',
        	});
		} else {
			self.send('update', {
				'id': 'output',
            	'property': 'html',
            	'content': '<b>No tags found</b>',
        	});
			// Put the message in the upper right.
            self.send('update', {
            	'id': 'overlay',
            	'property': 'style',
            	'content': 'top:0%;left:0%',
            });
		}
		self.send('update', {
       		'id': 'input',
       		'property': 'html',
       		'content': '',
       	});
	});

	this.addInputHandler('spec', function() {
		var content = self.get('spec');
       	self.send('update', {
       		'id': 'input',
       		'property': 'html',
       		'content': content,
       	});
	});

	this.addInputHandler('image', function() {
		var image = self.get('image');
		// Send the new image, updating the resource named 'image.jpg'.
		self.send('resources', {
			'image.jpg': {
				'data': image,
				'contentType': 'image/jpeg'
			}
		});
		// Send an update to force the browser to update the DOM img element.
		self.send('update', {
			'id':'image',
			'property':'src',
			'content':'image.jpg'
		});
		var html = '<h1>Augmented Reality Demo</h1>\n';

		html += '<form action="/" method="post" target="blankIframe">\n\
				<input type="submit" value="Next Tag" name="next" id="next"/>\n\
			</form>\n\
			<div id="container">\n\
		    <img src="image.jpg" id="image" alt="Mechanical Room Image">\n\
		    <div id="overlay" style="top:0%;left:0%;">\n\
		        <div id="output">No data</div><br/>\n\
		        <div id="input"></div>\n\
		    </div>\n\
		</div>\n';

		// Firefox insists on displaying the response to the post,
		// so we direct the response to a small iframe.
		html += '<iframe name="blankIframe" id="blankIframe" height=0 width=0/>';

		if (!htmlSent) {
			self.send('html', html);
			htmlSent = true;
		}
	});

	this.addInputHandler('translate', function() {
		var translate = self.get('translate');
		if (translate !== null && translate[0] !== null && translate[1] !== null) {
            var id = "overlay";
            var property = "style";
            // FIXME: Hardcoded to container width of 1000px and Height 700.
            // Assume image is not scaled.
            var left = 100*translate[0]/1000;
            var top = 100*translate[1]/700;
            var content = "top:" + top + "%;left:" + left + "%;";
            self.send('update', {
            	'id': id,
            	'property': property,
            	'content': content,
            });
        }
	});

	this.addInputHandler('data', function() {
		var id = "output";
        var property = "html";
        var content = '<table>';

        var json = self.get('data');
		try {
			json = JSON.parse(json);
		} catch(err) {
			json = {'error': err.message};
		}

		if (!json || Object.keys(json).length === 0) {
			content += '<tr><td>No Data</td></tr>';
		} else {
			// If the keys have changed, then print a wait message.
			var keys = '';
			for (prop in json) {
				keys += prop + ',';
			}
			for (prop in json) {
				var value = util.inspect(json[prop]);
				content += '<tr><td>' + prop + '</td><td>' + value + '</td></tr>';
			}
		}
		content += '</table>';

        self.send('update', {
        	'id': id,
           	'property': property,
           	'content': content,
        });
	});
}

// On each trigger input, if the tags input is a non-empty array of tags,
// this script outputs an id for the tag with the least numerical id.
// Whenever it receives an empty *next* input, then upon the next trigger
// input, it advances to the next least id for a visible tag, wrapping
// around to the overal smallest visible id one when it has scrolled
// through the tags. Whenever the tag changes, it outputs 'new' on reset.
// When it  receives an empty tags input for more than countForReset triggers,
// then it outputs 'none' on reset.

exports.setup = function() {
	this.input('trigger');
    this.input('tags');
    this.input('next');

    this.output('reset', {'type': 'string'});
    this.output('id', {'type': 'int'});

    this.parameter('countForReset', {'type':'int', 'value':5});
};

exports.initialize = function() {
    var thiz = this;
    var id = -1;
    var first = true;
    var nextTag = false;
    this.absentCount = 0;

    this.addInputHandler('trigger', function() {
        var tags = thiz.get('tags');

        if (!tags || tags.length === 0) {
        	// If we have seen enough frames with no tags, send a reset.
        	var countForReset = thiz.getParameter('countForReset');
        	if (thiz.absentCount >= countForReset) {
        		thiz.send('reset','none');
        		if (!first) {
        			// Signal downstream to unreify the accessor.
        			thiz.send('id', -1);
        		}
        		// Set first to true in case a tag reappears later.
        		first = true;
        		return;
        	}
        	thiz.absentCount += 1;
        	return;
        }
        thiz.absentCount = 0;

        tags.sort( function(a,b){
            if (a.id < b.id){
                return -1;
            }
            if (a.id > b.id){
                return 1;
            }
            return 0;
        });


        if (first && tags && tags.length > 0) {     
            id = tags[0].id;
            thiz.send('id', id );
            first = false;
            thiz.send('reset', 'new');
            return;  
        }

        if (nextTag) {
            nextTag = false;
            // Even if there is only one tag, the previous ID may be missing.
            if (tags && tags.length > 0) {
            	// By default, choose the first in the list.
            	var newId = tags[0].id;
                // Find the next smallest tag ID greater than the current one.
                for(var i = 0; i < tags.length; i++){
                    if(tags[i].id > id){
                        newId = tags[i].id;
                        break;
                    }
                }
                if (newId !== id) {
					id = newId;
                	thiz.send('id', id);
                	thiz.send('reset', 'new');
                }
            }
        }
    });   

    this.addInputHandler('next', function() {
        var next = thiz.get('next');
        if (typeof next === 'string') {
            next = JSON.parse(next);
        }
        if (!next || (Object.keys(next).length === 0 
                && next.constructor === Object)) {
            // Empty object or null received.
            nextTag = true;
        }
    });
};

// This script accepts a JSON schema (see http://json-schema.org/)
// and produces HTML for entering data that conforms with the specified
// schema. At this point, it is very limited, supporting only a small
// subset of the schema. But it is also extended to allow for hints
// to provide more natural interfaces such as pushbuttons instead of
// entry boxes. The most basic functionality takes a schema like this:
//    { "type": "object",
//      "properties": {
//         "a": {
//            "type": "string",
//            "title": "A",
//            "description": "A description here",
//			  "choices": ["first", "second"]
//          }
//         "b": {
//             ...
//          }
//       }
// and generates an HTML table with entry boxes labeled with the specified
// title and a "submit" button.
//
// If a property has a "choices" property whose value is an array of
// strings, then a radio button will be used instead of an entry box
// with the specified choices offered as options.
//
// If a property has no "choices" property but instead has an "options"
// property whose value is an array of strings, then a drop-down list
// will br provided instead of an entry box.
exports.setup = function() {
	this.input('schema', {'type':'JSON'});
	this.output('html', {'type':'string'});
}
exports.initialize = function() {
	var self = this;
	this.addInputHandler('schema', function() {
		var schema = self.get('schema');
		var content = '<h2>Inputs:</h2><form action="/" method="post" target="blankIframe" id="form"/><table>';

        // FIXME: Handle types other than object, e.g. string, number.      	
        if (schema.type === 'object') {
			for (prop in schema.properties) {
				var name = prop;
				var entry = schema.properties[prop];
				if (entry['title']) {
					name = entry['title'];
				}
				// Default type of entry will be a string.
				// Note that this is not the same as the type in the schema,
				// which specifies a JSON type, not an HTML5 type.
				var choices = entry['choices'];
				var options = entry['options'];
				var input = '';
				if (choices && choices.length) {
					for (var i = 0; i < choices.length; i++) {
						input += '<input type="radio" name="'
							+ prop
							+ '" value="'
							+ choices[i]
							+ '" form="form"/>'
							+ choices[i];
					}
				} else if (options && options.length) {
					input += '<select form="form" name="'
							+ prop
							+ '">';
					for (var i = 0; i < options.length; i++) {
						input += '<option value="'
							+ options[i]
							+ '">'
							+ options[i]
							+ '</option>';
					}
					input += '</select>';
				} else {
					input = '<input type="text" name="'
							+ prop
							+ '" form="form"/>';
				}

				content += '<tr><td>'
						+ name
						+ '</td><td>'
						+ input
						+ '</td></tr>';
			}
		}
		content += '</table><input type="submit" value="Submit" form="form">';

		self.send('html', content);
	});
}
