/** This accessor subscribes to a pre-established ROS topic.<br>
 *  It communicates to ROS through the rosbridge web socket, and extends the
 *  WebSocketClient accessor to do so.
 *  It has a 'topic' parameter, that must be prefixed with a '/' eg: '/noise'.<br>.
 *  The other parameters configure how the data is to be received according
 *  to the rosbridge specification:
 *  https://github.com/RobotWebTools/rosbridge_suite/blob/develop/ROSBRIDGE_PROTOCOL.md#344-subscribe
 *
 *  @accessor robotics/RosSubscriber
 *  @parameter {string} topic The ROS topic to subscribe to.
 *  @parameter {int} throttleRate The minimum amount of time (in ms)
 *   that must elapse between messages sent. Defaults to 0.
 *  @parameter {int} queueLength The ROS size of the queue to buffer messages.
 *   Messages are buffered as a result of the throttleRate. Defaults to 1.
 *  @parameter {int} fragment_size The maximum size that a message can take
 *   before it is to be fragmented. Defaults to 1000. Ptolemy will close the
 *   model if fragment size is too large (not sure what the maximum is).
 *  @parameter {string} compression A string to specify the compression
 *   scheme to be used on messages. Options are "none" (default) and "png".
 *  @parameter {boolean} outputCompleteResponseOnly A flag which if set to true
 *   will cause the accessor to delay in sending messages on the "received" port
 *   until it has concatenated the data fields from message fragments back into
 *   the original unfragmented message. Otherwise it will send the message
 *   fragments as they come in.
 *  @output {boolean} connected The status of the web socket connection.
 *  @output {JSON} received The data received from the web socket server.
 *  @author Marcus Pan, Matt Weber
 *  @version $$Id$$
 */

// Stop extra messages from jslint and jshint.  Note that there should
// be no space between the / and the * and global. See
// https://chess.eecs.berkeley.edu/ptexternal/wiki/Main/JSHint */
/*globals console, getParameter, exports, extend, parameter, send */
/*jshint globalstrict: true*/
'use strict';

/** Sets up by accessor by inheriting inputs, outputs and parameters from setup() in WebSocketClient.<br>
 *  Adds a 'topic' input which is the ROS topic to subscribe to. */
exports.setup = function () {

    this.extend('net/WebSocketClient');

    this.parameter('topic', {
        type: "string",
        value: ""
    });
    this.parameter('throttleRate', {
        type: "int",
        value: 0
    });
    this.parameter('queueLength', {
        type: "int",
        value: 10
    });
    this.parameter('fragmentSize', {
        type: "int",
        value: 10000
    });
    this.parameter('outputCompleteResponseOnly', {
        type: "boolean",
        value: true
    });
    this.parameter('compression', {
        type: "string",
        value: 'none'
    });
};

/** Overrides the toSendInputHandler to throw an error if called.
 *  A subscriber should not be publishing inputs.
 */
exports.toSendInputHandler = function () {
    console.error('This is a subscriber and does not take input to publish.');
};

/** Inherits initialize from webSocketClient.
 *  Sends a message to rosbridge to start subscribing to the topic on input 'topic'.
 */
exports.initialize = function () {
    this.exports.ssuper.initialize.call(this);

    this.exports.sendToWebSocket.call(this, {
        "op": "subscribe",
        "topic": this.getParameter('topic'),
        "throttle_rate": this.getParameter('throttleRate'),
        "queue_length": this.getParameter('queueLength'),
        "fragment_size": this.getParameter('fragmentSize'),
        "compression": this.getParameter('compression')
    });
};

/** Unsubscribe from the topic. Close websocket connections by calling wrapup of WebSocketClient */
exports.wrapup = function () {
    var unsubscribe = {
        "op": "unsubscribe",
        "topic": this.getParameter('topic')
    };
    this.exports.sendToWebSocket.call(this, unsubscribe);
    this.exports.ssuper.wrapup.call(this);
};

//Combines fragments into the original message. If the message is incomplete this function
//returns null. When the entire message has been received it returns the whole message.
exports.defragmentMessage = (function () {

    //This closure remembers the number and content of fragments already seen.
    var originalMessage = "",
        fragmentCount = 0,
        processMessage = function (message) {

            //Check for missing fragment
            if (fragmentCount !== message.num) {
                console.error("Fragment " +
                    fragmentCount +
                    " of message is missing. Instead received fragment number " +
                    message.num);
            }

            //Accumulate data from fragment.
            if (fragmentCount === 0) {
                originalMessage = message.data;
                fragmentCount += 1;
                return null;
            } else if (fragmentCount < message.total - 1) {
                originalMessage += message.data;
                fragmentCount += 1;
                return null;
            } else if (fragmentCount == message.total - 1) {
                originalMessage += message.data;
                fragmentCount = 0;
                return originalMessage;
            } else {
                console.error("Error in reconstructing fragments. Fragment count exceeds indicated total.");
                return null;
            }
        };
    return processMessage;
})();


exports.onMessage = function (message) {

    var messageToSend;
    if (this.getParameter('outputCompleteResponseOnly') && message.op === "fragment") {
        messageToSend = this.defragmentMessage(message);
        if (messageToSend === null) {
            return;
        }
    } else {
        messageToSend = message;
    }

    this.send('received', messageToSend);
};

/**
 *  <p>This accessor takes in the current pose of the Scarab, and a lightMap which gives the lightID's 
 *  and positions of available Hue lights in JSON form.</p>
 *  <pre>
 *  lightMap = { 
 *    "lights": [
 *                {"id": 1, "position": {x, y, z}}, 
 *                {"id": 2, "position": {x, y, z}}
 *              ]
 *  } </pre>
 *  <p>At a given interval, it gets the closest light to the Scarab's current
 *  position. If this light is closer than a set threshold, it turns on this light to the given setting.  *  This interval, threshold and the light settings are parameters.
 *  parameters.</p> 
 *
 *  @accessor ScarabHueController
 *  @input {JSON} pose The current pose of the Scarab, formatted according to the ROS datatype geometry_msgs/Pose.
 *  @parameter {JSON} lightMap The coordinates and light id's of the Hue bulbs.
 *  @parameter {integer} checkLightsInterval The interval, in milliseconds, to find the nearest light bulb to the Scarab. 
 *  @parameter {number} brightness Brightness of the light (number between 0-255). 
 *  @parameter {number} hue Hue of the light (number between 0 - 62580). 
 *  @parameter {number} saturation Saturation of the light (number between 0-255).
 *  @parameter {integer} transitionTime Transition time between light states, in multiples of 100 ms.
 *  @parameter {number} distanceThreshold. If the closest light's distance is less than distanceThresholdin m, turn the light on.
 *  @output {integer} lightID id of the light to turn on. 
 *  @output {number} brightnessOut Brightness of the light (number between 0-255). 
 *  @output {number} hueOut Hue of the light (number between 0 - 62580). 
 *  @output {number} saturationOut Saturation of the light (number between 0-255).
 *  @output {boolean} onOut whether the light is on (true) or off (false). 
 *  @output {integer} transitionTimeOut Transition time between light states, in mulitples of 100 ms. 
 *
 */

var lightMap;
var handle;

/** Define inputs, parameters and outputs. */
exports.setup = function() {
  this.input('pose');
  this.parameter('lightMap', {
    type: 'JSON', 
    value: {}
  });
  this.parameter('brightness', {
    type: 'number',
    value: 255
  });
  this.parameter('on', {
    type: 'boolean',
    value: false
  });
  this.parameter('hue', {
    type: 'number',
    value: 1
  });
  this.parameter('saturation', {
    type: 'number',
    value: 254
  });
  this.parameter('transitionTime', {
    type: 'int',
    value: 1
  });
  this.parameter('checkLightsInterval', {
    type: 'int', 
    value: 1000
  });
  this.parameter('distanceThreshold', {
    type: 'number',
    value: 1
   });
  this.output('lightIDOut', {
    type: 'int'
  });
  this.output('brightnessOut', {
    type: 'number'
  });
  this.output('hueOut', {
    type: 'number'
  });
  this.output('saturationOut', {
    type: 'number'
  });
  this.output('onOut', {
    type: 'boolean'
  });
  this.output('transitionTimeOut', {
    type: 'int'
  });
 this.output('trigger', {
    type: 'boolean'
  });
}

/** check parameters and set interval to check lights */
exports.initialize = function() {
  lightMap = this.getParameter('lightMap');
  if (lightMap == null || lightMap.lights == null ) {
     throw "Invalid light map";
   }
  handle = setInterval(checkLights, this.getParameter('checkLightsInterval'));
}

/** get closest light and send light data */
function checkLights() {
  var pose = this.get('pose');
  //make sure pose isn't empty
  if (Object.keys(pose) == 0) {
    console.log('no pose yet');
    return;
  }
  var sortedLights = []; //array of lights sorted by closest distance to Scarab
  var nearestLight = {"id": null, "distance": null};

  //get nearest light
  for (var i = 0; i < lightMap.lights.length; i++) {
    var light = lightMap.lights[i];
    var dx = Math.pow(pose.msg.pose.position.x-light.position[0], 2);
    var dy = Math.pow(pose.msg.pose.position.y-light.position[1], 2);
    var dz = Math.pow(pose.msg.pose.position.z-light.position[2], 2);
    var distance = Math.sqrt(dx+dy+dz);
    if (nearestLight.distance == null || distance < nearestLight.distance) {
       nearestLight.id = light.id;
       nearestLight.distance = distance;
    }
  }

  //send light information
  if (nearestLight.distance < this.getParameter('distanceThreshold')) {
    this.send('lightIDOut', nearestLight.id);
    this.send('transitionTimeOut', this.getParameter('transitionTime'));
    this.send('onOut', this.getParameter('on'));
    this.send('hueOut', this.getParameter('hue'));
    this.send('brightnessOut', this.getParameter('brightness'));
    this.send('saturationOut', this.getParameter('saturation'));
    this.send('trigger', true);
  }
}

/** clear timeout interval. */
exports.wrapup = function() {
  clearTimeout(handle);
}
