/** Remove info that can vary from response.
 *
 *  @accessor RemoveSomeInfo
 *  @input input Remove info that can vary from the response.
 *  @output output The HTTP response without variable info.
 *  @author Elizabeth Osyk
 *  @version $$Id$$
 */
exports.setup = function() {
    this.input('input');
    this.output('output');
};

exports.initialize = function() {
    var self = this;
    this.addInputHandler('input', function() {
       var data = self.get('input');
       var result = data;
       if (data !== null) {
       	  if (typeof data === 'string' || data instanceof String) {
	          // Remove the "origin" (i.e. IP address) since this will differ per machine
	          var start = data.indexOf("origin");
	          var end;

	          if (start >= 0) {
	       	     end = data.indexOf("url");
	       	     result = data.substring(0, start) + data.substring(end, data.length);
	          }

	          // Remove "Connect-Time" header field
	          var start = result.indexOf("Connect-Time");
	          if (start >= 0) {
	          	end = result.indexOf(",", start);
	          	result = result.substring(0, start) + result.substring(end, result.length);
	          }

	          // Remove "Total-Route-Time" header field
	          var start = result.indexOf("Total-Route-Time");
	          if (start >= 0) {
	          	end = result.indexOf(",", start);
	          	result = result.substring(0, start) + result.substring(end, result.length);
	          }

	          // Remove "Via" header field
	          var start = result.indexOf("Via");
	          if (start >= 0) {
	          	end = result.indexOf(",", start);
	          	result = result.substring(0, start) + result.substring(end, result.length);
	          }

	          // Remove "X-Request-Id" header field
	          var start = result.indexOf("X-Request-Id");
	          if (start >= 0) {
	          	end = result.indexOf("}", start);
	          	result = result.substring(0, start) + result.substring(end - 1, result.length);
	          }
       	  } else if (typeof data === 'object') {
       	  	if (data.hasOwnProperty('origin')) {
       	  		delete data.origin;
       	  	}
       	  }
       }
	   self.send('output', result);
    });
}

/** Emit a true if all of the inputs have received a true.
 *  If the timeout is non-zero, then emit a true after the timeout.
 *
 *  This accessor is typically used to stop a model after all
 *  of the TrainableTest accessors have emitted a true.
 *
 *  @input input1 The first input
 *  @input input2 the second input
 *  @output output True if the inputs have seen true or if the
 *  timeout has occurred
 *  @param timeout If non-zero, the timeout in milliseconds.
 *  @author Christopher Brooks
 *  @version $$Id$$
 */

// Stop extra messages from jslint and jshint.  Note that there should
// be no space between the / and the * and global. See
// https://chess.eecs.berkeley.edu/ptexternal/wiki/Main/JSHint */
/*globals console, exports*/
/*jshint globalstrict: true*/
/*jslint plusplus: true */
'use strict';

// After inputs appear on both inputs, generate a true output.
var sawInput1 = false;
var sawInput2 = false;

exports.setup = function() {
    this.input('input1');   
    this.input('input2'); 
    this.output('output', {
    	'type': 'boolean'
    });
    this.parameter('timeout');

    var self = this;
    self.addInputHandler('input1', function() {
    	self.sawInput1 = this.get('input1');
    	if (self.sawInput1 == true && self.sawInput2 === true) {
    	    self.send('output', true);
    	}
    });
    self.addInputHandler('input2', function() {
	self.sawInput2 = this.get('input2');
 	if (self.sawInput1 == true && self.sawInput2 === true) {
    	    self.send('output', true);
    	}
    });
}

exports.initialize = function() {
    var self = this;

    if (this.getParameter('timeout') > 0) {
        setTimeout(function () {
	    console.log('JavaScriptBarrier: sending true after timeout.');
	    self.send('output', true);
        }, this.getParameter('timeout'));
    }
}
