"use strict";

exports.setup = function () {
    this.input('count');
    this.input('received');
    this.output('output', {
        'type': 'string'
    });
};

exports.fire = function () {
	var count = this.get('count');
	var received = this.get('received');
    this.send('output', "Server resp #" + count + " to \"" + received + "\"");
};

/** Generate JSON with the hostname and port name.
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals console, error, exports, require */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.input('trigger');
    this.output('output', {
        'type': 'JSON',
        'value': {host: 'localhost', port: 21200}
    });
};

exports.fire = function () {
    this.send('output', {host: 'localhost', port: 21200});
};

/** Generate discrete events at prespecified time instants.    
 *
 *  @accessor JavaScriptDiscreteClock
 *
 *  @input trigger The trigger
 *  @output output The output
 *  @author Christopher Brooks
 *  @version $$Id$$
 */
exports.setup = function() {
    this.input('start');
    this.input('stop');
    this.input('trigger');
    this.output('output', {'type':'int'});
};

var _enabled = false;
var _handle = null;


exports.initialize = function() {
    var self = this;
    this.addInputHandler("start", function () {
    	console.log('JSDC: got start');
    	self._enabled = true;
    	self.send('output', 1);
    	_handle = setInterval(function () {
    		if (self._enabled) {
				self.send('output', 1);
    		}
        }, 0.5 * 1000.0);
        // Stop after 2.6 seconds.
        setTimeout(function () {
        	console.log('JSDC: about to disable');
    		self._enabled = false;
    		if (self._handle) {
        		clearInterval(self._handle);
       			self._handle = null;
    		}	
        }, 2600);
 	 });
    this.addInputHandler("stop", function () {
    	self._enabled = false;
    	if (self._handle) {
        	clearInterval(self._handle);
       		self._handle = null;
    	}	
 	 });
}

exports.wrapup = function () {
    if (_handle) {
        clearInterval(_handle);
        _handle = null;
    }
};
