var socket = require('socket');

exports.setup = function() {
	this.input('toSend');
	this.output('reply');
	this.parameter(
		'receiveType',
		{'value':'string', 'type':'string',
		 'options':socket.supportedReceiveTypes()}
	);
	this.parameter(
		'sendType',
		{'value':'string', 'type':'string',
		 'options':socket.supportedSendTypes()}
	);
}

exports.initialize = function() {
	var client = new socket.SocketClient(
		4021,
		'localhost',
		{'receiveType': this.getParameter('receiveType'),
		 'sendType': this.getParameter('sendType')}
	);
	var self = this;
	this.addInputHandler('toSend', function() {
		var data = self.get('toSend');
		console.log('Sending data: ' + data);
		client.send(data);
	});
	client.on('data', function(data) {
		self.send('reply', data);
	});
	client.open();
}

var socket = require('socket');

exports.setup = function() {
	this.output('listening');
	this.output('connected');
	this.output('received');
	this.parameter(
		'receiveType',
		{'value':'string',
		 'type':'string',
		 'options':socket.supportedReceiveTypes()}
	);
	this.parameter(
		'sendType',
		{'value':'string',
		 'type':'string',
		 'options':socket.supportedSendTypes()}
	);
}

var server = null;
var connectionCount = 0;

exports.initialize = function() {
	server = new socket.SocketServer({
		'port':4021,
		'hostInterface':'localhost',
		'receiveType': this.getParameter('receiveType'),
		'sendType': this.getParameter('sendType')
	});
	var self = this;
	server.on('listening', function(port) {
		self.send('listening', port);
	});
	server.on('connection', function(serverSocket) {
		connectionCount++;
		self.send('connected', connectionCount);
		serverSocket.on('data', function(data) {
			self.send('received', data);
			serverSocket.send(data + 1);
		});
	});
	server.start();
}

exports.wrapup = function() {
	if (server) {
		server.stop();
	}
}

